        if DEBUG_FUNC_FLAG:
            utils.debug_time('mwn 14518 on_video_catalogue_sort_combo_changed')

        tree_iter = self.catalogue_sort_combo.get_active_iter()
        model = self.catalogue_sort_combo.get_model()
