        self.checkbutton11 = Gtk.CheckButton()
        grid.attach(self.checkbutton11, 1, 4, 1, 1)
        self.checkbutton11.set_label(_('Delete all description files'))

        self.checkbutton12 = Gtk.CheckButton()
