        button = Gtk.Button.new_with_label(_('Select all'))
        grid.attach(button, 0, 7, 1, 1)
        button.set_hexpand(False)
        # (Signal connect appears below)

        button2 = Gtk.Button.new_with_label(_('Select none'))
